import React, { Component } from "react";
import axios from "axios";

class UserComponent extends Component {
  state = {
    id: this.props.match.params.id,
    person: [],
    error: false,
    loading: true,
  };

  componentDidMount() {
    axios
      .get(`https://jsonplaceholder.typicode.com/users/${this.state.id}`)
      .then((res) => {
        console.log("res:", res);
        this.setState({ persons: res.data, loading: false }, () => {
          console.log("state: ", this.state.persons);
        });
      })
      .catch((error) => {
        this.setState({ error: true, loading: false });
      });

    console.log(this.state.persons);
  }
  render() {
    return (
      <div>
        SingleUser: {this.state.id}
        <span>name : {this.state.persons.name || "N/A"}</span>
        <span>email : {this.state.persons.email || "N/A"}</span>
        <span>phone : {this.state.persons.phone || "N/A"}</span>
        <span>username : {this.state.persons.username || "N/A"}</span>
        {/* <button
          className="btn btn-success"
          onClick={() => this.component(this.state.id)}
        >
          View
        </button> */}
      </div>
    );
  }
}

export default UserComponent;
